import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import {FormBuilder, FormGroup} from "@angular/forms"
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  public loginForm!: FormGroup
  constructor(private formBuilder : FormBuilder, private http : HttpClient, private router:Router){}

  ngOnInit(): void {
    this.loginForm = this.formBuilder.group({
      email:[''],
      password:['']
    })
  }
  login(){
    this.http.get<any>("http://localhost:3000/signupUsers")
    .subscribe(res=>{
      const user = res.find((a:any)=>{
        return a.email === this.loginForm.value.email && a.password === this.loginForm.value.password
      });
      if(user){
        alert("Login Success!!");
        this.loginForm.reset();
        this.router.navigate(['product'])
      }else{
        alert("user not found!!");
      }
    },err=>{
      alert("Something went wrong!!")
    })

  }

}
<div class="container">
    <div class="row">
        <div class="col-md-6">
            <div class="card">
                <div class="text-center">
                    <h1>Login</h1>
                    <h6>Please enter email & password !!</h6>
                </div>
                <form>
                    <form [formGroup]="loginForm" (ngSubmit)="login()">
                        <div class="mb-3">
                          <label for="email" class="form-label">Email address</label>
                          <input formControlName="email" type="email" class="form-control" id="email" minlength="4" aria-describedby="emailHelp" required="email">
                          <span class="error">Please enter your Email</span>

                        </div>
                        <div class="mb-3">
                          <label for="exampleInputPassword1" class="form-label">Password</label>
                          <input formControlName="password" type="password" class="form-control" id="exampleInputPassword1">
                          <span class="error">Please enter a valid email address</span>

                        </div>
                        <button type="submit" class="btn btn-primary">Login</button>
                    </form>
                    <a style="color: #fff; margin-top: 10px;" routerLink="/signup">New user? Click to signup!!</a>
                </form>
            </div>
        </div>
    </div>
</div>
