  //     productName: [''],
  //     productDescription: [''],
  //     productPrice: [''],
  //     createdDate: [''] // Added createdDate field to the form
  //   });
  // }



ngOnInit(): void{
this.formValue = this.formbuilder.group({
  productName : [''],
  productDescription : [''],
  productPrice : ['']
})
this.getAllProduct();
}
postProductDetails(){
  this.productModelObj.productName = this.formValue.value.productName;
  this.productModelObj.productDescription = this.formValue.value.productDescription;
  this.productModelObj.productPrice = this.formValue.value.productPrice;
console.log("this.productModelObj",this.productModelObj)
  this.api.postProduct(this.productModelObj)
  .subscribe(res=>{
    console.log(res);
    alert("Product Added Successfully")
    let ref = document.getElementById('cancel')
    ref?.click();
    this.formValue.reset();
   },
  err=>{
    alert("Something went wrong");
  })
}
getAllProduct(){
  this.api.getProduct()
  .subscribe(res=>{
    this.productData = res;
  })
}
  // Function to get current date and time
  getCurrentDateTime(): string {
    const currentDate = new Date();
    return this.datePipe.transform(currentDate, 'yyyy-MM-dd HH:mm:ss');
  }

}
